      out.write("\r\n");
      out.write("// 각각 자기 자신의 sub메뉴를 제외하고 다른 것을 눌렀을때 서브메뉴가 올라가는 메소드\r\n");
      out.write("  $('html').click(function(e){ \r\n");
      out.write("   \r\n");
      out.write("     \r\n");
      out.write("     if( $(\"#boardDiv\").data(\"value\") ==\"1\"){ \r\n");
      out.write("      if(!$(e.target).hasClass(\"boardDown\")){  \r\n");
      out.write("         if($('#boardDiv').data(\"value\") == \"1\"){\r\n");
      out.write("            $(\"#boardDiv\").slideUp(); \r\n");
      out.write("            $(\"#boardDiv\").data(\"value\", \"0\");\r\n");
      out.write("            $('.boardMenu').removeClass(\"hoverStay\");\r\n");
      out.write("            $(\"#blackOpacity1\").fadeOut(\"fast\");\r\n");
      out.write("         }\r\n");
      out.write("      } \r\n");
      out.write("    }\r\n");
      out.write("   \r\n");
      out.write("     \r\n");
      out.write("     if( $(\"#mypageDiv\").data(\"value\") ==\"1\"){ \r\n");
      out.write("         if(!$(e.target).hasClass(\"mypageDown\")){  \r\n");
      out.write("            if($('#mypageDiv').data(\"value\") == \"1\"){\r\n");
      out.write("               $(\"#mypageDiv\").slideUp(); \r\n");
      out.write("               $(\"#mypageDiv\").data(\"value\", \"0\");\r\n");
      out.write("               $('.mypageMenu').removeClass(\"hoverStay\");\r\n");
      out.write("               $(\"#blackOpacity2\").fadeOut(\"fast\");\r\n");
      out.write("            }\r\n");
      out.write("         } \r\n");
      out.write("        }\r\n");
      out.write("  \r\n");
      out.write("  \r\n");
      out.write("     if( $(\"#teacherDiv\").data(\"value\") ==\"1\"){ \r\n");
      out.write("         if(!$(e.target).hasClass(\"teacherDown\")){  \r\n");
      out.write("            if($('#teacherDiv').data(\"value\") == \"1\"){\r\n");
      out.write("               $(\"#teacherDiv\").slideUp(); \r\n");
      out.write("               $(\"#teacherDiv\").data(\"value\", \"0\");\r\n");
      out.write("               $('.teacherMenu').removeClass(\"hoverStay\");\r\n");
      out.write("               $(\"#blackOpacity3\").fadeOut(\"fast\");\r\n");
      out.write("            }\r\n");
      out.write("         }\r\n");
      out.write("        }\r\n");
      out.write("  \r\n");
      out.write("     if( $(\"#loginDiv\").data(\"value\") ==\"1\"){ \r\n");
      out.write("         if(!$(e.target).hasClass(\"loginDivShow\")){  \r\n");
      out.write("            \r\n");
