      out.write("  \r\n");
      out.write("// 각각의 div 토글\r\n");
      out.write("   function showBoardDiv(){\r\n");
      out.write("      if($('#boardDiv').data(\"value\") == \"0\"){\r\n");
      out.write("         $('.boardMenu').addClass(\"hoverStay\"); \r\n");
      out.write("         $('.boardMenu').addClass(\"boardDown\");\r\n");
      out.write("         $('#boardDiv').addClass(\"boardDown\");\r\n");
      out.write("         $('#boardDiv').children().addClass(\"boardDown\");    \r\n");
      out.write("         $('#boardDiv').children().children().addClass(\"boardDown\");             \r\n");
      out.write("         $(\"#boardDiv\").slideDown(); \r\n");
      out.write("         $(\"#blackOpacity1\").fadeIn(\"fast\");\r\n");
      out.write("         $(\"#boardDiv\").data(\"value\", \"1\");\r\n");
      out.write("      }else{\r\n");
      out.write("         $(\"#boardDiv\").slideUp();  \r\n");
      out.write("         $(\"#boardDiv\").data(\"value\", \"0\");\r\n");
      out.write("         $('.boardMenu').removeClass(\"hoverStay\");\r\n");
      out.write("         $(\"#blackOpacity1\").fadeOut(\"fast\");\r\n");
      out.write("      }\r\n");
