      out.write("            $('#mypageDiv').children().children().addClass(\"mypageDown\");             \r\n");
      out.write("            $(\"#mypageDiv\").slideDown(); \r\n");
      out.write("            $(\"#mypageDiv\").data(\"value\", \"1\");\r\n");
      out.write("            $(\"#blackOpacity2\").fadeIn(\"fast\");\r\n");
      out.write("         }else{\r\n");
      out.write("            $(\"#mypageDiv\").slideUp();  \r\n");
      out.write("            $(\"#TeacherDiv\").data(\"value\", \"0\");\r\n");
