      out.write("           $(\".loginDiv\").css(\"display\",\"block\")\r\n");
      out.write("           $(\"#loginIcon\").addClass(\"loginDivShow\");\r\n");
      out.write("           $(\".loginDiv\").addClass(\"loginDivShow\");\r\n");
      out.write("           $(\".loginDiv\").children().addClass(\"loginDivShow\");\r\n");
      out.write("           $(\".loginDiv\").children().children().addClass(\"loginDivShow\");\r\n");
      out.write("           $(\".loginDiv\").children().children().children().addClass(\"loginDivShow\");\r\n");
      out.write("           $(\"#blackOpacity4\").fadeIn('2000');\r\n");
      out.write("           $(\".loginDiv\").animate({\"right\":\"0px\"},'2000',function(){\r\n");
      out.write("           $(\"#loginDiv\").data(\"value\",\"1\"); \r\n");
      out.write("           }); \r\n");
      out.write("        }else{\r\n");
      out.write("             $(\"#blackOpacity2\").fadeOut('2000');\r\n");
      out.write("              $(\".loginDiv\").animate({\"right\":\"-605px\"},'2000',function(){\r\n");
      out.write("                 $(\".loginDiv\").css(\"display\",\"none\") \r\n");
      out.write("              });\r\n");
      out.write("              $(\"#blackOpacity4\").fadeOut('2000');   \r\n");
      out.write("              $(\"#loginDiv\").data(\"value\",\"0\");\r\n");
      out.write("              $(\"#loginIcon\").removeClass(\"hoverStay\") \r\n");
